type MinHeap [][2]int

func (h MinHeap) Len() int            { return len(h) }
func (h MinHeap) Less(i, j int) bool  { return h[i][0] < h[j][0] }
func (h MinHeap) Swap(i, j int)       { h[i], h[j] = h[j], h[i] }
func (h *MinHeap) Push(x interface{}) { *h = append(*h, x.([2]int)) }
func (h *MinHeap) Pop() (v interface{}) {
	*h, v = (*h)[:len(*h)-1], (*h)[len(*h)-1]
	return
}

type Graph struct {
    gr map[int][][2]int
    n int
}


func Constructor(n int, edges [][]int) Graph {
    gr := make(map[int][][2]int, n)
    for _, edge := range edges {
		u, v, w := edge[0], edge[1], edge[2]
        gr[u] = append(gr[u], [2]int{w, v})
    }
    return Graph{
        gr:gr,
        n:n,
    }
}


func (g *Graph) AddEdge(edge []int)  {
    u, v, w := edge[0], edge[1], edge[2]
    g.gr[u] = append(g.gr[u], [2]int{w, v})
}

const inf = 1 << 31
func (g *Graph) ShortestPath(st int, ed int) int {
	dist := make([]int, g.n)
	for i := range dist {
		dist[i] = inf
	}
	dist[st] = 0
	h := &MinHeap{}
	heap.Init(h)
	h.Push([2]int{0, st})

	used := make([]bool, g.n)

