class Graph {
    public int vertices;
    public Map<Integer, List<int[]>> adjList;
    public Graph(int n, int[][] edges) {
        this.vertices = n;
        this.adjList = new HashMap<>();
        for(int i=0; i<edges.length; i++){
            int[] edge = edges[i];
            if(!adjList.containsKey(edge[0])){
                adjList.put(edge[0], new ArrayList<>());
            }
            adjList.get(edge[0]).add(new int[]{edge[1], edge[2]});
        }
    }

    public void addEdge(int[] edge) {
        if(!adjList.containsKey(edge[0])){
                adjList.put(edge[0], new ArrayList<>());
        }
        adjList.get(edge[0]).add(new int[]{edge[1], edge[2]});
    }

    public int shortestPath(int node1, int node2) {
        boolean[] sptSet = new boolean[vertices];
        for(int i=0; i<vertices; i++){
            sptSet[i] = false;
        }

        int[] shortestPath = new int[this.vertices];
        for(int i=0; i<vertices; i++){
            shortestPath[i] = Integer.MAX_VALUE;
        }
        shortestPath[node1] = 0;
        util(node1, sptSet, shortestPath);
        if(shortestPath[node2] == Integer.MAX_VALUE){
            return -1;
        }else{
            return shortestPath[node2];
        }
    }

    public void util(Integer node, boolean[] sptSet, int[] shortestPath){

        Map<Integer, List<int[]>> temp = this.adjList;

        for(int i=0; i<this.vertices; i++){
            int currentNode = getNewNode(sptSet, shortestPath);
            if(currentNode == -1){
                continue;
            }
            int currentShort = shortestPath[currentNode];
            List<int[]> neigs = adjList.getOrDefault(currentNode, new ArrayList<>());
            sptSet[currentNode] = true;

            for(int j =0; j< neigs.size(); j++){
                int[] neig = neigs.get(j);
                int dest = neig[0];
                int cost = neig[1];
                if(currentShort + cost < shortestPath[dest]){
                    shortestPath[dest] = currentShort + cost;
                }
            }
        }
    }

    public int getNewNode(boolean[] sptSet, int[] shortestPath){
        int index = -1;
        int min = Integer.MAX_VALUE;
        for(int i=0; i<this.vertices; i++){
            if(sptSet[i] == false && shortestPath[i] < min){
                min = shortestPath[i];
                index = i;
            }
        }
        return index;
    }
}

