class Graph(_n: Int, _edges: Array[Array[Int]]) {

  type Vertex = Int

  val edgeMap = scala.collection.mutable.Map.empty[Vertex, List[(Vertex, Int)]].withDefaultValue(List.empty)
  _edges.foreach(addEdge)

  def addEdge(arr: Array[Int]): Unit =
    edgeMap(arr(0)) ::= (arr(1), arr(2))

  def shortestPath(node1: Int, node2: Int): Int = {
    val visited = scala.collection.mutable.Set.empty[Vertex]
    val queue = scala.collection.mutable.PriorityQueue((0, node1)).reverse
    def goFind: Int =
      if(queue.isEmpty) -1
      else {
        val (cost, vertex) = queue.dequeue()
        if (!visited.add(vertex)) goFind
        else if (vertex == node2) cost
        else {
          edgeMap(vertex).foreach { case (to, edgeCost) => queue.enqueue((cost + edgeCost, to)) }
          goFind
        }
      }

    goFind
  }
}
