class Graph {
public:
// creation of an adjaceny list.
    unordered_map<int, vector<pair<int, int> > > adj;
    int count;
    Graph(int n, vector<vector<int>>& edges) {
        //intialization of object
        //called intially for the creation of graph
        for(auto x : edges){
            adj[x[0]].push_back({x[1], x[2]});
            // this is simple graph creation, kind of an adjacency list
        }
        count = n;
    }

    void addEdge(vector<int> edge) {
        // basically we need to add the graph edge with the corresponding weight
        // so we've created a graph and just map (u,v) with the corresponding edge weight
        adj[edge[0]].push_back({edge[1], edge[2]});
    }

    int shortestPath(int node1, int node2) {
        // to find the shortest path, we use dijkstra's : striver approach of set

        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int> > > pq;
        pq.push({0,node1}); // 0,0;
        vector<int> dist(count+1, 1e9);
        dist[node1] = 0; // dist[S] = 0

        while(!pq.empty()){
            int dis = pq.top().first;
            int node = pq.top().second;
            pq.pop();

            for(auto it : adj[node]){
                if(dis + it.second < dist[it.first]){
                    dist[it.first] = dis + it.second;
                    pq.push({dist[it.first], it.first});
                }
            }
        }

        // now we check that whether the node2 was reachable or not,,
        // if not return -1
        // else the distance array gives you the distance from node1 to node2
        if(dist[node2] == 1e9) return -1;
        else return dist[node2];
    }
};

