class Graph {
public:
    vector<vector<pair<int,int>>> adj;

    Graph(int n, vector<vector<int>>& edges) {
        adj.resize(n);
       for(auto &i: edges){
           adj[i[0]].push_back({i[1], i[2]});
       }

    }

    void addEdge(vector<int> edge) {
        adj[edge[0]].push_back({edge[1], edge[2]});
    }

    int shortestPath(int node1, int node2) {
        set<pair<int,int>> q;
        q.insert({0,node1});
        vector<int> visited(adj.size(), 0);
        vector<int> dist(adj.size(), 1e9);
        dist[node1]=0;

        while(!q.empty()){
            pair<int,int> node = *q.begin();
            visited[node.second]=1;
            q.erase(q.begin());

            for(auto &i: adj[node.second]){
                if(!visited[i.first]){
                    dist[i.first]=min(dist[i.first], i.second+node.first);
                    q.insert({dist[i.first], i.first});
                }
            }
        }

        if(dist[node2]==1e9){
            return -1;
        }
        return dist[node2];   
    }
};

