class Graph {
    public HashMap<Integer,HashMap<Integer,Integer>> map=new HashMap<>();
    public Graph(int n, int[][] edges) {
        for(int i=0;i<n;i++){
            map.put(i,new HashMap<>());
        }
        for(int i=0;i<edges.length;i++){
            map.get(edges[i][0]).put(edges[i][1],edges[i][2]);
        }
    }

    public void addEdge(int[] edge) {
        map.get(edge[0]).put(edge[1],edge[2]);
    }

    public int shortestPath(int node1, int node2) {
        PriorityQueue<pairs> pq=new PriorityQueue<>(new Comparator<pairs>() {
            @Override
            public int compare(pairs o1,pairs o2){
                return o1.cost-o2.cost;
            }
        });
        HashSet<Integer> visited=new HashSet<>();
        pq.add(new pairs(node1,0));
        while(!pq.isEmpty()){
            pairs rv=pq.remove();
            if(visited.contains(rv.vtx)){
                continue;
            }
            if(rv.vtx==node2){
                return rv.cost;
            } 
            visited.add(rv.vtx);
            for(int nbrs:map.get(rv.vtx).keySet()){
                if(!visited.contains(nbrs)){
                    pq.add(new pairs(nbrs,rv.cost+map.get(rv.vtx).get(nbrs)));
                }
            }
        }
        return -1;
    }
}
class pairs{
        int vtx;
        int cost;
        pairs(int e1,int cost){
            this.vtx=e1;
            this.cost=cost;
        }
    }


