class Graph {

    private HashMap<Integer, HashMap<Integer, Integer>> map;

    public Graph(int n, int[][] edges) {
        map = new HashMap<>();
        for (int i = 0; i < n; i++) {
            map.put(i, new HashMap<>());
        }
        for (int[] edge : edges) {
            map.get(edge[0]).put(edge[1], edge[2]);
        }
    }

    public void addEdge(int[] edge) {
        map.get(edge[0]).put(edge[1], edge[2]);
    }

    private int findMinimum(boolean[] visited, int[] costMap) {
        int minVal = Integer.MAX_VALUE, min = -1;
        for (int i = 0; i < costMap.length; i++) {
            if (!visited[i]) {
                if (minVal > costMap[i]) {
                    minVal = costMap[i];
                    min = i;
                }
            }
        }
        return min;
    }

    public int shortestPath(int node1, int node2) {
        boolean[] visited = new boolean[this.map.size()];
        int[] costMap = new int[this.map.size()];
        Arrays.fill(costMap, Integer.MAX_VALUE);
        costMap[node1] = 0;
        for (int i = 0; i < this.map.size() - 1; i++) {
            int key = findMinimum(visited, costMap);
            if (key == -1) {
                break;
            }
            visited[key] = true;
            if (this.map.containsKey(key)) {
                for (int to : this.map.get(key).keySet()) {
                    costMap[to] = Math.min(costMap[to], costMap[key] + this.map.get(key).get(to));
                }
            }
        }
        if (costMap[node2] == Integer.MAX_VALUE) {
            return -1;
        }
        return costMap[node2];
    }
}

