class Graph {
  N: number;
  edges: number[][];

  constructor(n: number, edges: number[][]) {
    this.N = n;
    this.edges = [];
    for(let i = 0; i < n; i++) {
      this.edges[i] = [];
      for(let j = 0; j < n; j++) {
        if (i === j) {
          this.edges[i][j] = 0;
        } else {
          this.edges[i][j] = Number.MAX_SAFE_INTEGER;
        }
      }
    }

    edges.forEach(([x, y, w]) => {
      this.edges[x][y] = Math.min(this.edges[x][y], w);
    });

    for (let k = 0; k < n; k++) {
      for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
          if (i != k && j != k && i != j) {
            this.edges[i][j] = Math.min(this.edges[i][j], this.edges[i][k] + this.edges[k][j]);
          }
        }
      }
    }
  }

  addEdge(edge: number[]): void {
    for (let i = 0; i < this.N; i++) {
      for (let j = 0; j < this.N; j++) {
        this.edges[i][j] = Math.min(this.edges[i][j], this.edges[i][edge[0]] + edge[2] + this.edges[edge[1]][j]);
      }
    }
  }

  shortestPath(node1: number, node2: number): number {
    return this.edges[node1][node2] === Number.MAX_SAFE_INTEGER ? -1 : this.edges[node1][node2];
  }
}
