/**
 * @param {number} n
 * @param {number[][]} edges
 */
var Graph = function(n, edges) {
    this.n = n;
    this.map = new Map();
    let nodes;
    for (const [from, to, cost] of edges) {
        nodes = this.map.get(from);
        if (nodes) nodes.push({ to: to, cost: cost });
        else this.map.set(from, [{ to: to, cost: cost }]);
    }
};

/** 
 * @param {number[]} edge
 * @return {void}
 */
Graph.prototype.addEdge = function(edge) {
    const [from, to, cost] = edge;
    const nodes = this.map.get(from);
    if (nodes) nodes.push({ to: to, cost: cost });
    else this.map.set(from, [{ to: to, cost: cost }]);
};

/** 
 * @param {number} node1 
 * @param {number} node2
 * @return {number}
 */
Graph.prototype.shortestPath = function(node1, node2) {
    const dist = new Array(this.n).fill(Number.MAX_VALUE);
    dist[node1] = 0;

    const stack = [{ to: node1, cost: 0 }];

    while (stack.length) {
        const { to, cost } = stack.shift();
        const nodes = this.map.get(to);
        if (nodes) {
            for (let i = 0; i < nodes.length; ++i) {
                if (cost + nodes[i].cost < dist[nodes[i].to]) {
                    dist[nodes[i].to] = cost + nodes[i].cost;
                    stack.push({ to: nodes[i].to, cost: dist[nodes[i].to] });
                }
            }
        }
    }

    return dist[node2] === Number.MAX_VALUE ? -1 : dist[node2];
};

