        u, v, w = edge
        self.adj_list[u].append((v, w))


    def shortestPath(self, node1: int, node2: int) -> int:

        n = len(self.adj_list)
        dist = [float('inf')] * n
        dist[node1] = 0
        heap = [(0, node1)]

        while heap:
            d, u = heapq.heappop(heap)
            if u == node2:
                return d
            if dist[u] < d:
                continue
            for v, w in self.adj_list[u]:
                if dist[u] + w < dist[v]:
                    dist[v] = dist[u] + w
                    heapq.heappush(heap, (dist[v], v))

