#include <vector>
#include <queue>
#include <limits>

using namespace std;

class Graph {
private:
    vector<vector<pair<int, int>>> adj_list;
    vector<int> dist;
    vector<bool> visited;
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;

public:
    Graph(int n, vector<vector<int>>& edges) {
        adj_list.resize(n);
        dist.resize(n, numeric_limits<int>::max());
        visited.resize(n, false);

        for (auto& edge : edges) {
            int from = edge[0];
            int to = edge[1];
            int cost = edge[2];
            adj_list[from].push_back(make_pair(to, cost));
        }
    }

    void addEdge(vector<int> edge) {
        int from = edge[0];
        int to = edge[1];
        int cost = edge[2];
        adj_list[from].push_back(make_pair(to, cost));
    }

    int shortestPath(int node1, int node2) {
        while (!pq.empty()) {
            pq.pop();
        }
        dist.assign(dist.size(), numeric_limits<int>::max());
        visited.assign(visited.size(), false);

        dist[node1] = 0;
        pq.push(make_pair(0, node1));

        while (!pq.empty()) {
            int node = pq.top().second;
            pq.pop();

            if (visited[node]) {
                continue;
            }

            visited[node] = true;

            for (auto& neighbor : adj_list[node]) {
                int neighbor_node = neighbor.first;
                int neighbor_cost = neighbor.second;

                if (visited[neighbor_node]) {
                    continue;
                }

                int tentative_dist = dist[node] + neighbor_cost;

                if (tentative_dist < dist[neighbor_node]) {
                    dist[neighbor_node] = tentative_dist;
                    pq.push(make_pair(tentative_dist, neighbor_node));
                }
            }
        }

        return (dist[node2] == numeric_limits<int>::max()) ? -1 : dist[node2];
    }
};

```

