    Graph(int n, vector<vector<int>>& edges) {
        numNodes = n;
        adjList.resize(numNodes);

        for (auto& edge : edges) {
            int from = edge[0];
            int to = edge[1];
            int cost = edge[2];
            adjList[from].push_back({to, cost});
