import heapq
class Graph:
    #the graph constructor 
    def __init__(self, n: int, edges: List[List[int]]):
        self.n = n
        self.edges = edges 


    def addEdge(self, edge: List[int]) -> None:
        self.edges.append(edge)

    #one extra function to create the directed, weighted graph which gets called only when all the edges have been inserted 
    def createGraph(self,edges):
        graph = {i:dict() for i in range(self.n)}

        for s,e,w in edges:
            graph[s][e] = w

        return graph
    #implementation of the dijkstra algorithm 
    def shortestPath(self, node1: int, node2: int) -> int:
        graph = self.createGraph(self.edges)
        dists = {node:math.inf for node in graph}
        dists[node1] = 0
        q = [(0,node1)]

        while q:
            curr_dist, curr_node = heapq.heappop(q)
            if curr_dist > dists[curr_node]:
                continue
            else:
                for nbr, wt in graph[curr_node].items():
                    dist = curr_dist + wt
                    if dist < dists[nbr]:
                        dists[nbr] = dist
                        heapq.heappush(q,(dist,nbr))
        return -1 if dists[node2] == math.inf else dists[node2]


