class Graph {
   int n;
   ArrayList<ArrayList<int[]>> l;
    public Graph(int n, int[][] edges) {
        this.n=n;
        l=new ArrayList<>();
        for(int i=0;i<n;i++)
        {
            l.add(new ArrayList<>());
        }
        for(int edge[]:edges)
        {
          int from=edge[0];
          int to=edge[1];
          int cost=edge[2];
          l.get(from).add(new int[]{to,cost});
        }


    }

    public void addEdge(int[] edge) {

          int from=edge[0];
          int to=edge[1];
          int cost=edge[2];
          l.get(from).add(new int[]{to,cost});

    }

    public int shortestPath(int node1, int node2) {
        PriorityQueue<int[]> pq=new PriorityQueue<>((a,b)->a[1]-b[1]);
        pq.add(new int[]{node1,0});
        int distance[]=new int[n];
        Arrays.fill(distance,Integer.MAX_VALUE);
        distance[node1]=0;
        while(!pq.isEmpty())
        {
            int currNode=pq.peek()[0];
            int currDistance=pq.peek()[1];
            pq.poll();
            if(node2==currNode)return distance[currNode];
            ArrayList<int[]> child=l.get(currNode);
            for(int []c:child){
                int nextNode=c[0];
                int childdist=c[1];
                if(childdist+currDistance<distance[nextNode])
                {
                    distance[nextNode]=childdist+currDistance;
                    pq.add(new int[]{nextNode,distance[nextNode]});
                }
            }

        }
        return -1;
    }
}

