    }
}

//it is stored in queue to represent node with it's current distance
class Pair{
    int node,distance;
    Pair(int node,int distance){
        this.node=node;
        this.distance=distance;
    }
}

class Graph {
    //adjacency list for graph
    ArrayList<ArrayList<Node>> adj=new ArrayList<>();

    public Graph(int n, int[][] edges) {
        //adding empty array for all "n" nodes
        for(int i=0;i<n;i++){
            ArrayList<Node> arr=new ArrayList<>();
            adj.add(arr);
        }
        //creating edges between two nodes with given edge weight
        for(int i=0;i<edges.length;i++){
            adj.get(edges[i][0]).add(new Node(edges[i][1],edges[i][2]));
        }
    }

    //creating edges between two nodes with given edge weight
    public void addEdge(int[] edges) {
        adj.get(edges[0]).add(new Node(edges[1],edges[2]));
    }

    public int shortestPath(int node1, int node2) {
        //min-heap to store node with minimum path distance
        PriorityQueue<Pair> pq=new PriorityQueue<Pair>((x,y)-> x.distance-y.distance);
        pq.offer(new Pair(node1,0));
        int[] dist=new int[adj.size()];
        //declaring distance array with all values intially as infinity
        for(int i=0;i<dist.length;i++){
            dist[i]=(int)(1e9);
        }
        //source node distance is assigned to 0
        dist[node1]=0;
        while(pq.size()!=0){
            //removing pair from queue
            Pair p=pq.poll();
            int distance=p.distance;
            int node=p.node;
            //traversing neighbouring nodes 
            for(int i=0;i<adj.get(node).size();i++){
                int adjacent=adj.get(node).get(i).adjacent;
                int edgeCost=adj.get(node).get(i).edgeCost;
                //if computed path distance is smaller,update it
                if(distance+edgeCost<dist[adjacent]){
                    dist[adjacent]=distance+edgeCost;
                    pq.add(new Pair(adjacent,dist[adjacent]));
                }
            }
        }
        //if distance of destination node is infinity,it can be reached and return -1
        //else return the distance
        return dist[node2]==(int)(1e9)?-1:dist[node2];
    }
}

