class Graph {
 private:
  static constexpr int i_from = 0;
  static constexpr int i_to = 1;
  static constexpr int i_cost = 2;
  using pq_node_t = pair<int, int>;   // {node, minimum cost}

 public:
  /**
   * Time Complexity: O(n_edges)
   */
  Graph(const int n, const vector<vector<int>> &edges) : graph_(n) {
    for (const vector<int> &edge : edges) {
      graph_[edge[i_from]].emplace_back(edge[i_to], edge[i_cost]);
    }
  }

  /**
   * Time Complexity: O(1)
   */
  void addEdge(const vector<int> &edge) {
    graph_[edge[i_from]].emplace_back(edge[i_to], edge[i_cost]);
  }

  /**
   * Time Complexity: O(n_edges * log(n))
   */
  int shortestPath(const int node1, const int node2) {
    auto compare = [](const pq_node_t &lhs, const pq_node_t &rhs) -> bool {
      return !(lhs.second < rhs.second);
    };

    priority_queue<pq_node_t, vector<pq_node_t>, decltype(compare)> pq(compare);
    pq.emplace(node1, 0);
    int costs[graph_.size()];
    fill(costs, costs + graph_.size(), numeric_limits<int>::max());
    while (!pq.empty()) {
      const auto [node, cost] = pq.top();
      pq.pop();
      if (node == node2) {
        return cost;
      }
      if (cost >= costs[node]) {
        continue;
      }
      costs[node] = cost;
      for (const auto [next, cost_delta] : graph_[node]) {
        const int next_cost = cost + cost_delta;
        if (next_cost >= costs[next]) {
          continue;
        }
        pq.emplace(next, next_cost);
      }
    }
    return -1;
  }

 private:
  vector<vector<pair<int, int>>> graph_;
};
```

