        public Node(int node,int cost){
            this.node = node;
            this.cost = cost;
        }
    }

    class Cell implements Comparable<Cell>{
        int node;
        int minCost;
        public Cell(int node,int minCost){
            this.node = node;
            this.minCost = minCost;
        }
        public int compareTo(Cell that){
            return this.minCost - that.minCost;
        }
    }

    List<List<Node>> adjList = new ArrayList<>();
    int n;

    public Graph(int n, int[][] edges) {

        this.n = n;

        for(int i=0;i<n;i++){
            adjList.add(new ArrayList<>());
        }
        for(int[] edge:edges){
            adjList.get(edge[0]).add(new Node(edge[1],edge[2]));

        }

    }

    public void addEdge(int[] edge) {

            if(adjList.contains(edge[0])){
                adjList.add(new ArrayList<>());
            }

            adjList.get(edge[0]).add(new Node(edge[1],edge[2]));  

    }

    public int shortestPath(int node1, int node2) {

        Queue<Cell> q = new PriorityQueue<>();

        q.add(new Cell(node1,0));


        int[] shortestPath = new int[n];

        Arrays.fill(shortestPath,Integer.MAX_VALUE);

        shortestPath[node1] = 0;

        while(!q.isEmpty()){

            Cell cell = q.poll();

            int node = cell.node;
            int minCost = cell.minCost;

            for(int i=0;i<adjList.get(node).size();i++){
                int neighbourNode = adjList.get(node).get(i).node;
                int neighbourNodeCost = adjList.get(node).get(i).cost;

                if(shortestPath[neighbourNode] > minCost + neighbourNodeCost){
                    shortestPath[neighbourNode] = minCost + neighbourNodeCost;
                    q.add(new Cell(neighbourNode,shortestPath[neighbourNode]));
                }

            }


        }

        if(shortestPath[node2] == Integer.MAX_VALUE)
            return -1;

        return shortestPath[node2];
    }
}
