        for (int i = 0; i< n; ++i)dis[i][i] = 0;   
        for (int k = 0; k < n; ++k)
             for (int i = 0; i < n; ++i)
               for (int j = 0; j < n; ++j)
                      dis[i][j] = min(dis[i][j], dis[i][k]+dis[k][j]);
    }
    void addEdge(vector<int> e) {
        int k1 = e[0], k2 = e[1], c = e[2];
        for (int i = 0; i < n; ++i)
            for (int j = 0; j < n; ++j)
                dis[i][j] = min(dis[i][j], dis[i][k1] + c + dis[k2][j]);        
