class Pair implements Comparable<Pair> {
    int node;
    int wt;
    public Pair(int node, int wt){
        this.node=node;
        this.wt=wt;
    }
    @Override
    public int compareTo(Pair p){
        return this.wt==p.wt?(this.node-p.node):(this.wt-p.wt);
    }
}

class Graph {

    List<List<Pair>> adjList=new ArrayList<>();

    public Graph(int n, int[][] edges) {
        for(int i=0; i<n; i++) adjList.add(new ArrayList<Pair>());
        for(int[] edge: edges){
            int u=edge[0];
            int v=edge[1];
            int c=edge[2];
            adjList.get(u).add(new Pair(v, c));
        }
    }

    public void addEdge(int[] edge) {
        int u=edge[0];
        int v=edge[1];
        int c=edge[2];
        adjList.get(u).add(new Pair(v, c));
    }

    public int shortestPath(int node1, int node2) {
        Queue<Pair> q=new PriorityQueue<>();
        int [] dist=new int [adjList.size()];
        Arrays.fill(dist, Integer.MAX_VALUE);
        dist[node1]=0;
        q.add(new Pair(node1,0));
        while(!q.isEmpty()){
            Pair pair=q.poll();
            int curr=pair.node;
            for(Pair adj: adjList.get(curr)){
                int adjNode=adj.node;
                int wt=adj.wt;
                if(dist[adjNode]>dist[curr]+wt){
                    dist[adjNode]=dist[curr]+wt;
                    q.add(new Pair(adjNode, dist[adjNode]));
                }
            }
        }
        return dist[node2]==Integer.MAX_VALUE ? -1 : dist[node2];

    }
}

