class Graph {
public:
    vector<vector<pair<int,int>>>adj;

    Graph(int n, vector<vector<int>>& edges) {
        adj.resize(n);
        for(auto& edge:edges){
            adj[edge[0]].push_back({edge[1],edge[2]});
        }

    }

    void addEdge(vector<int> edge) {
        if(edge.size()==2){
            edge.push_back(0);
        }
        adj[edge[0]].push_back({edge[1],edge[2]});
    }

    int shortestPath(int src, int dest) {
    int n = adj.size(); // Number of nodes in the graph
    vector<int> dist(n, INT_MAX); // Initialize distances to all nodes as infinity
    dist[src] = 0; // Distance from source to itself is 0

    // Priority queue to store nodes to be processed, ordered by distance
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    pq.push({0, src});

    while (!pq.empty()) {
        int u = pq.top().second; // Extract the node with minimum distance
        pq.pop();

        // Iterate over all adjacent nodes of u
        for (auto& edge : adj[u]) {
            int v = edge.first; // Adjacent node
            int weight = edge.second; // Weight of the edge from u to v

            // If a shorter path is found from src to v passing through u
            if (dist[u] + weight < dist[v]) {
                dist[v] = dist[u] + weight; // Update the distance
                pq.push({dist[v], v}); // Push v into the priority queue for further processing
            }
        }
    }

    if(dist[dest]==INT_MAX) return -1;
        else return dist[dest]; 
    }
};

