class Graph {

    List<MyPath>[] allPaths;
    int level;

    public Graph(int n, int[][] edges) {
        allPaths = new ArrayList[n];
        level = n;
        for(int[] edge : edges){
            int from = edge[0];
            if(allPaths[from] == null)
                allPaths[from] = new ArrayList<>();
            allPaths[from].add(new MyPath(edge[1], edge[2]));
        }
    }

    public void addEdge(int[] edge) {
        int from = edge[0];
        if(allPaths[from] == null)
            allPaths[from] = new ArrayList<>();
        allPaths[from].add(new MyPath(edge[1], edge[2]));
    }

    public int shortestPath(int node1, int node2) {

        Queue<MyPath> q = new LinkedList<>();
        int[] distance = new int[level];

        q.add(new MyPath(node1, 0));
        int res = Integer.MAX_VALUE;

        while(!q.isEmpty()){
            int size = q.size();
            for(int i = 0; i < size; i++){
                MyPath node = q.poll();
                if(node.getDst() == node2){
                    res = Math.min(res, node.getCost());
                    continue;
                }
                int cur = node.getDst();
                if(allPaths[cur] != null){
                    for(MyPath neighbor : allPaths[cur]){
                      if(distance[neighbor.getDst()] != 0 && neighbor.getCost() + node.getCost() > distance[neighbor.getDst()])
                            continue;
                        distance[neighbor.getDst()] = neighbor.getCost() + node.getCost();
                        q.add(new MyPath(neighbor.getDst(), neighbor.getCost() + node.getCost()));
                    }
                }
            }
        }

        return res == Integer.MAX_VALUE ? -1 : res;
    }
}

class MyPath{
    int dst;
    int cost;

    MyPath(final int dst, final int cost){
        this.dst = dst;
        this.cost = cost;
    }

    public int getDst(){
        return this.dst;
    }

    public int getCost(){
        return this.cost;
    }
}

