class Graph {
public:
    vector<vector<pair<int,int>>> graph;
    Graph(int n, vector<vector<int>>& edges) {
        graph.resize(n,vector<pair<int,int>>());
        for(int i=0;i<edges.size();i++)
        {
            graph[edges[i][0]].push_back({edges[i][1],edges[i][2]});
        }
    }

    void addEdge(vector<int> edge) {
        graph[edge[0]].push_back({edge[1],edge[2]});
    }

    int shortestPath(int node1, int node2) {
        vector<int> distance(graph.size(),INT_MAX);
        distance[node1]=0;
        queue<pair<int,int>> q;
        q.push({node1,0});
        while(q.size()!=0)
        {
            pair<int,int> temp=q.front();
            q.pop();
            if(distance[temp.first]<temp.second)continue;
            for(int i=0;i<graph[temp.first].size();i++)
            {
                int new_distance=distance[temp.first]+graph[temp.first][i].second;
                if(new_distance>=distance[graph[temp.first][i].first])continue;
                distance[graph[temp.first][i].first]=new_distance;
                q.push({graph[temp.first][i].first,new_distance});
            }
        }
        return  distance[node2]==INT_MAX?-1:distance[node2];
    }
};
