class Graph {

    public class Edge{
        int src;
        int dest;
        int weight;
        Edge(int src,int dest, int weight){
            this.src = src;
            this.dest = dest;
            this.weight = weight;
        }
    }
    int len;
    ArrayList<Edge>[] al;
    public Graph(int n, int[][] edges) {
        al = new ArrayList[n];
        len = n;
        for(int i=0;i<n;i++){
            al[i] = new ArrayList<Edge>();
        }
        for(int[] edge : edges){
            al[edge[0]].add(new Edge(edge[0],edge[1],edge[2]));
        }
    }

    public void addEdge(int[] edge) {
        al[edge[0]].add(new Edge(edge[0],edge[1],edge[2]));
    }

    public int shortestPath(int node1, int node2) {
        PriorityQueue<Edge> pq = new PriorityQueue<>((Edge a,Edge b)->{
            return a.weight-b.weight;
        });
        int[] dist = new int[len];
        Arrays.fill(dist, Integer.MAX_VALUE);
        dist[node1] = 0;
        pq.offer(new Edge(node1, node1, 0));
        while (!pq.isEmpty()) {
            Edge node = pq.poll();
            int u = node.dest;
            if (dist[u] < node.weight) continue;
            for (Edge neighbor : al[u]) {
                int v = neighbor.dest;
                int wei = neighbor.weight;
                if (dist[v] > dist[u] + wei) {
                    dist[v] = dist[u] + wei;
                    pq.offer(new Edge(node1, v, dist[v]));
                }
            }
        }
        return dist[node2] == Integer.MAX_VALUE ? -1 : dist[node2];
    }

}

