class Graph:

    def __init__(self, n: int, edges: List[List[int]]):
        self.__n = n
        self.__adjL = [ list() for _ in range(n) ]

        for edge in edges:
            self.__adjL[edge[0]].append([edge[1],edge[2]])

    def addEdge(self, edge: List[int]) -> None:
        self.__adjL[edge[0]].append([edge[1],edge[2]])

    def shortestPath(self, node1: int, node2: int) -> int:
        adjL = self.__adjL

        visited = [ False for _ in range(self.__n) ]

        heap = []
        heap.append([0, node1])

        while heap:
            dist, cur = heapq.heappop(heap)

            if visited[cur]:
                continue

            visited[cur] = True

            if cur == node2:
                return dist

            for nextNode, weight in adjL[cur]:
                if not visited[nextNode]:
                    heapq.heappush(heap, [weight+dist, nextNode])

        return -1


