class Graph {
public:

    int dijkstra(int start,int dst,vector<pair<int,int>> adj[],int n){
        set<pair<int,int>> st;
        st.insert({0,start});
    vector<int> dis(n,INT_MAX);
        dis[start]=0;
        while(st.size()>0){
            auto temp=*(st.begin());
            if(temp.second==dst) return dis[dst];
            st.erase(temp);
            for(auto it:adj[temp.second]){
                if(dis[temp.second]+it.second<dis[it.first]){
                    if(dis[it.first]!=INT_MAX){
                        st.erase({dis[it.first],it.first});
                    }
                    dis[it.first]=dis[temp.second]+it.second;
                    st.insert({dis[temp.second]+it.second,it.first});
                }
            }
        }
        return -1;
    }
    int num;
    vector<pair<int,int>> adj[101];
