class Graph {

    //int[] -> child, cost
    HashMap<Integer, List<int[]>> g = new HashMap<>();

    public Graph(int n, int[][] edges) {
        for(int i=0; i<n; i++){
            g.put(i, new ArrayList<int[]>());
        }

        for(int[] edge : edges){
            addEdge(edge);
        }
    }

    //from, to, edgecost
    public void addEdge(int[] edge) {
        //to, edgecost
        g.get(edge[0]).add(new int[]{edge[1], edge[2]});
    }

    public int shortestPath(int node1, int node2) {
        int[] dst = new int[g.size()];
        boolean[] visited = new boolean[g.size()];

        Arrays.fill(dst, Integer.MAX_VALUE);

        if(node1 == node2) return 0;

        Queue<Integer> q = new ArrayDeque();
        q.offer(node1);
        dst[node1] = 0;
        visited[node1] = true;

        while(!q.isEmpty()){
            int size = q.size();
            for(int i=0; i<size; i++){

                //parent
                int par = q.poll();

                //child: to, edgecost
                for(int[] child : g.get(par)){
                    int to = child[0];
                    int cost = child[1];

                    int prevdst = dst[to];
                    //dst at child
                    dst[to] = Math.min(dst[to], dst[par] + cost);

                    if(prevdst != dst[to]){
                        //visited[to] = true;
                        q.offer(to);
                    }
                }

            }
        }

        return dst[node2] == Integer.MAX_VALUE ? -1 : dst[node2];
    }
}

