class Solution {
public:
    int numOfMultiples(int n, int m){
        if(n<m){return 0;}
        else{
            int mults = 0; 
            for(int i = 1; i<n+1; i++){
                if(i%m == 0){mults+=i;}
            }
            return mults; 
        }
    }
    int sumOfMultiples(int n) {
        return numOfMultiples(n,3) + numOfMultiples(n,5) + numOfMultiples(n,7) - numOfMultiples(n,15) - numOfMultiples(n,35) - numOfMultiples(n,21) + numOfMultiples(n,105); 
    }
};
```

