```
/**
 * @param {string} val
 * @return {Object}
 */
var expect = function(val) {
    return {
     toBe: function(otherVal) {
            if (otherVal === val) {
                return true;
            } else {
                throw new Error("Not Equal"); // Throw an error instead of returning false
            }
        },
        notToBe: function(otherVal) {
            if (otherVal !== val) {
                return true;
            } else {
                throw new Error("Equal"); // Throw an error instead of returning false
            }
        }
  };
};

/**
 * expect(5).toBe(5); // true
 * expect(5).notToBe(5); // throws "Equal"
 */
