type ToBeOrNotToBe = {
    toBe: (val: any) => boolean;
    notToBe: (val: any) => boolean;
};

function expect(initialValue: any): ToBeOrNotToBe {
    return {
        toBe: (valueToCompare: any) => {
            const booleanResult = initialValue === valueToCompare

            // Early return
            if(booleanResult) 
                // Returning either the comparison or true yields the same result, you choose
                return booleanResult // always true

            throw new Error("Not Equal")
        },
        notToBe: (valueToCompare: any) => {
            const booleanResult = initialValue !== valueToCompare

            // Early return
            if(booleanResult) return booleanResult // always true

            throw new Error("Equal")
        }
    }
};

/**
 * expect(5).toBe(5); // true
 * expect(5).notToBe(5); // throws "Equal"
 */
```

