class Solution {
public:
    int addMinimum(string word) {
       int len = word.length();
        int ans = 0;
        int i = 0;
        while (i < len - 1) {
            char c = word[i];
            char cnex = word[i+1];
            if (c == 'a') {
                if (cnex == 'a')
