class Solution {
    public int addMinimum(String word) {
        if(word.equals("abc"))return 0;
        int count=0;
        int i=0;
        for(i=0;i<word.length()-1;i++){
            if(i+3<=word.length()){
                String h=word.substring(i,i+3);
                if(h.equals("abc")){
                i+=2;
                continue;
            }
        }
            String l=word.substring(i,i+2);
            if(l.equals("ab")||l.equals("bc")||l.equals("ac")){
                count++;
                i++;
                continue;
            }

            char ch=word.charAt(i);
            if(ch=='a'||ch=='b'||ch=='c')count+=2;
        }
        if(i==word.length()-1){
            char ch=word.charAt(i);
            if(ch=='a'||ch=='b'||ch=='c')count+=2;
        }
        return count;
    }
}
