            if(l.equals("ab")||l.equals("bc")||l.equals("ac")){
                count++;
                i++;
                continue;
            }

            char ch=word.charAt(i);
            if(ch=='a'||ch=='b'||ch=='c')count+=2;
        }
        if(i==word.length()-1){
            char ch=word.charAt(i);
            if(ch=='a'||ch=='b'||ch=='c')count+=2;
        }
        return count;
    }
}
