func addMinimum(word string) int {
    order, n := 0, 0
    pattern := "abc"

    for i := 0; i < len(word); {
        c := word[i]
        if pattern[order%3] != c {
            n++
        } else {
            i++
        }
        order++
    }

    n += (3 - order % 3) % 3

    return n
}
