class Solution {
public:
    int addMinimum(string word) {
        const int n = word.length();
        int r = 0;
        for (int i = 0; i < n; ++i) {
            switch(word[i]) {
                case 'c':
                    r += 2;
                    break;
                case 'b':
                    ++r;
                    if (i + 1 >= n || word[i + 1] != 'c') {
                        ++r;
                    } else {
                        ++i;
                    }
                    break;
                case 'a':
                    if (i + 1 >= n || word[i + 1] != 'b') {
                        ++r;
                    } else {
                        ++i;
                    }
                    if (i + 1 >= n || word[i + 1] != 'c') {
                        ++r;
                    } else {
                        ++i;
                    }
                    break;   
            }

        }
        return r;
    }
};
