class Solution {
    bool isSubsequence(const string &s, const string &w) {
        int j = 0;
        for (int i = j = 0; i < s.length() && j < w.length(); ++i) {
            if (s[i] == w[j]) {
                ++j;
            }
        }
        return j >= w.length();
    }
public:
    int addMinimum(string word) {
        string s;
        for (;!isSubsequence(s, word); s.append("abc"))
        ;
        return s.length() - word.length();
    }
};
```

