                if(s.top()=='a'){
                    if(w[i]=='a'){
                        cnt+=2;
                        s.pop();
                        s.push(w[i]);
                    }
                    else if(w[i]=='b'){
                        s.push(w[i]);
                    }
                    else{
                        cnt++;
                        s.pop();
                    }
                }
                else if(s.top()=='b'){
                    if(w[i]=='a'){
                        cnt++;
                        s.pop();
                        s.pop();
                        s.push(w[i]);
                    }
                    else if(w[i]=='b'){
                        cnt+=2;
                        s.pop();
                        s.push(w[i]);
                    }
                    else{
                        s.pop();
                        s.pop();
                    }
