impl Solution {
    pub fn add_minimum(word: String) -> i32 {
        let words: Vec<char> = word.chars().collect();
        let mut dp = vec![2; words.len()];
        for i in 1..words.len() {
            if words[i] > words[i - 1] {
                dp[i] = dp[i - 1] - 1;
            } else {
                dp[i] = dp[i - 1] + 2;
            }
        }
        dp[dp.len() - 1]
    }
}
