class Solution {
    public int addMinimum(String word) {
        char prev = 'c';
        int min = 0;
        for(char ch : word.toCharArray()) {
            if(ch == 'a' && prev != 'c') 
                min += prev == 'a' ? 2 : 1;
            else if(ch == 'b' && prev != 'a') 
                min += prev == 'c' ? 1: 2;
            else if(ch == 'c' && prev != 'b') 
                min += prev == 'c' ? 2 : 1;
            prev = ch;
        }
        if(prev == 'c') return min;
        return prev == 'a' ? min + 2 : min + 1;
    }
}
