class Solution {
    public int addMinimum(String word) {
        LinkedList<Character> wordStack = new LinkedList<Character>();
        for (Character c : word.toCharArray()) {
            wordStack.add(c);
        }
        LinkedList<Character> abcStack = new LinkedList<Character>();

        int j = 0;
        while (!wordStack.isEmpty()) {
            abcStack.add('a');
            abcStack.add('b');
            abcStack.add('c');

            while (!abcStack.isEmpty() && !wordStack.isEmpty()) {
                char c = wordStack.remove(0).charValue();
                char i = abcStack.remove(0).charValue();

                if (c != i) {
                    wordStack.add(0, c);
                    j++;
                }
            }

        }
        return j + abcStack.size();
    }
}


