    int addMinimum(string word) {

        int sum=int(word[0]-'0')-49,n=word.size();

        for(int i=1;i<n;i++)
        {
                //for each pair of sequential indices take their difference
            int diff=int(word[i-1]-'0')-int(word[i]-'0');
