def add_minimum(word: str) -> int:
    n = len(word)
    if (first := word[0]) == "a":
        ans = 0
    elif first == "b":
        ans = 1
    else:
        ans = 2

    for i in range(1, n):
        if word[i] == word[i - 1]:
            ans += 2
        elif (word[i - 1], word[i]) in (("a", "b"), ("b", "c"), ("c", "a")):
            continue
        else:
            ans += 1

    if word[-1] == "a":
        ans += 2
    elif word[-1] == "b":
        ans += 1

    return ans

