public class Solution {
    public int AddMinimum(string word) {
        int counter = 0;
        Stack<char> abc = new Stack<char>();
        abc.Push('a');
        abc.Push('b');
        abc.Push('c');
        foreach(char c in word){
            abc.Push(c);
        }
        if(abc.Peek() == 'a'){
            abc.Push('b');
            abc.Push('c');
            counter += 2;
        }
        if(abc.Peek() == 'b'){
            abc.Push('c');
            counter ++;
        }
        while(abc.Count > 2){
            if(abc.Peek() == 'c'){
                abc.Pop();
                if(abc.Peek() == 'a'){
                    counter++;
                    continue;
                }
                if(abc.Peek() == 'c'){
                    counter += 2;
                    continue;
                }
                if(abc.Peek() == 'b'){
                    continue;
