vector<int> distinctDifferenceArray(vector<int>& nums) {
    int cnt[51] = {}, dist = 0;
    vector<int> res;
    for (int n : nums)
        res.push_back((res.empty() ? 0 : res.back()) + (cnt[n]++ == 0));
    for (int i = nums.size() - 1; i >= 1; --i) {
        dist += exchange(cnt[nums[i]], 0) > 0;
        res[i - 1] -= dist;        
    }
    return res;
}
