class Solution:
    def distinctDifferenceArray(self, nums: List[int]) -> List[int]:
        pre, suf = [], []
        ps, ss = set(), set()
        n = len(nums)
        for i in range(n):
            ps.add(nums[i])
            pre.append(len(ps))
        for i in range(n - 1, -1, -1):
            suf.insert(0, len(ss))
            ss.add(nums[i])
        # pre: [1, 2, 3, 4, 5]
        # suf: [4, 3, 2, 1, 0]
        # ans: [-3, -1, 1, 3, 5]
        return [pre[i] - suf[i] for i in range(n)]
