const distinctDifferenceArray = function (a) {
  const n = a.length
  const pre = new Array(n) // prefix counts
  const suf = new Array(n) // suffix counts
  let set

  set = new Set()
  for (let i = 0; i < n; i++) {
    set.add(a[i])
    pre[i] = set.size
  }

  set = new Set()
  for (let i = n - 1; i >= 0; i--) {
    suf[i] = set.size
    set.add(a[i])
  }

  const an = new Array(n)
  for (let i = 0; i < n; i++) {
    an[i] = pre[i] - suf[i]
  }
  return an
}
