# Code
```
class Solution {
    public int[] distinctDifferenceArray(int[] nums) {

        int n = nums.length;
        int[] diff = new int[n];

        // iterate through each index
        for (int i = 0; i < n; i++) {
            int countPrefix = countDistinctElements(nums, 0, i);
            int countSuffix = countDistinctElements(nums, i + 1, n - 1);
            diff[i] = countPrefix - countSuffix;
        }

        return diff;
    }

    private static int countDistinctElements(int[] nums, int start, int end) {
        boolean[] visited = new boolean[100001]; // assuming values are less than 100001
        int count = 0;
        for (int i = start; i <= end; i++) {
            if (!visited[nums[i]]) {
                visited[nums[i]] = true;
                count++;
            }
        }
        return count;
}}
