        vector<int> pre, suf(nums.size());
        int count = 0;
        // prefix array
        for(auto i : nums) {
            if(s1.find(i) == s1.end()) count++;
            pre.push_back(count);
            s1.insert(i);
        }
        // suffix array
        count = 0;
        for(int i = nums.size()-1; i > 0; i--) {
            if(s2.find(nums[i]) == s2.end()) count++;
            suf[i - 1] = count;
            s2.insert(nums[i]);
        }
        // ans;
        for(int i = 0; i < nums.size(); i++) {
            pre[i] = pre[i] - suf[i];
        }
        return pre;
    }
};
