class Solution {
public:
    vector<int> distinctDifferenceArray(vector<int>& nums) {
        unordered_map<int,int>mp; // first we stored all the elements in the map 

        for(auto it:nums){
             mp[it]++;
        }
        set<int>st; // we used a set to store the prefix values
        vector<int>ans;
        for(auto it:nums){
           st.insert(it); // we inserted it into the set
           mp[it]--; // decremented the count of that element from the map used as suffix
           if(mp[it]==0) mp.erase(it); // if there was only one element then we delete it from suffix and the return the size of diff of prefix-suffix that is set-map and if there was more than one value of that element was present then it will exist in both pre and suffix 
           ans.push_back(st.size()-mp.size());
        }
        return ans;
    }
};
```

