class Solution {
public:
    int check(vector<int>& arr,int pos)
    {
        int hash1[51]={0};
        int hash2[51]={0};
        int first=pos;
        int leftdistinct=0,rightdistinct=0;
        int last=pos+1;
        while(first>=0)
        {
            if(hash1[arr[first]]==0)
            {hash1[arr[first]]=arr[first];
                leftdistinct++;
            }
            first--;
        }
        while(last<arr.size())
        {
            if(hash2[arr[last]]==0)
            {hash2[arr[last]]=arr[last];
                rightdistinct++;
            }
