class Solution {
public:
    vector<int> distinctDifferenceArray(vector<int>& v) {
        int n=v.size();
        map<int,int>m1,m2;
        for(auto i:v)
            m2[i]++;
        vector<int>ans;
        for(int i=0;i<n;i++)
        {
            m2[v[i]]--;
            if(m2[v[i]]==0)
                m2.erase(v[i]);
            m1[v[i]]++;
            ans.push_back(m1.size()-m2.size());
        }
        return ans;
    }
};
